mapper.select:
SELECT user_id, club_id, role
FROM club_user_list
WHERE club_id = #{club_id}

SELECT user_id, club_id, role
FROM club_user_list
WHERE user_id = #{user_id}

SELECT user_id, club_id, role
FROM club_user_list
WHERE user_id = #{user_id} AND club_id = #{club_id}

SELECT COUNT(*)
FROM CLUB_USER_LIST
WHERE club_id = #{club_id}
